import { useParams } from "react-router-dom"
import React, { useEffect, useState } from "react";
import TeamProfile from "../components/team/TeamProfile";
import LatestFuture from "../components/team/LatestFuture";
import PlayerCard from "../components/player/PlayerCard";
import ProvaApi from "../api/ProvaApi";
import News from "../components/news/News";
import Layout from "../components/Layout/Layout";
import Standings from "../components/standings/Standing";
import LeagueMatches from "../components/League/LeagueMatches";

const TeamDetail = (props) => {
    const {id} = useParams();
    const apiProva = new ProvaApi();
    const [teamData, setTeamData] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const fetchTeam = async () => {
            try {
                const responseTeamData = await apiProva.makeRequest(`_next/data/4276/en/teams/${id}/overview/inter.json?id=8636&tab=overview&slug=inter`);
                setTeamData(responseTeamData.data.pageProps);  
                console.log(teamData)  
                setIsLoading(false); 
            } catch (error) {
                console.log(error);
                setIsLoading(false);
            }
        };

        fetchTeam(); 

    }, [id]);

    console.log(teamData)

	return (
		<>
            <Layout />
            {isLoading ? 
            <div className="flex flex-col gap-3 p-4">
                <div role="status" className="animate-pulse h-64 bg-gray-200 rounded-lg flex"></div>
                <div className="flex gap-3">
                    <div role="status" className="animate-pulse h-90 bg-gray-200 rounded-lg"></div>
                    <div role="status" className="animate-pulse h-90 bg-gray-200 rounded-lg"></div>
                </div>
                <div role="status" className="animate-pulse h-64 bg-gray-200 rounded-lg"></div>
            </div>
            :
			<div className="text-black flex flex-col p-4 gap-5">
                <TeamProfile teamName={teamData.details?.name} teamNation={teamData.details?.country} teamLogo={`https://images.fotmob.com/image_resources/logo/teamlogo/${teamData.details?.id}.png` || ""} teamVenueName={teamData.details?.sportsTeamJSONLD?.location?.name || "Parc de Princes"} teamVenueCity={teamData.details?.sportsTeamJSONLD?.location?.address?.addressLocality || "Paris"}/>
                {/* <LatestFuture fixtureData={teamData.fixtures?.allFixtures}/> */}
                <LeagueMatches data={teamData}/>
                <div className="flex flex-col gap-2">
                    <div className="flex justify-between">
                        <div className="text-sm text-[#6d6a7f]">First Team</div>
                        <div className="text-sm text-[#6d6a7f]">2023-24</div>
                    </div>
                    <div className="overflow-x-auto flex gap-3">
                        {teamData?.squad?.map((squadElement, squadIndex) => {
                            let [position, players] = squadElement;
                            position = (position === "coach") ? "CH" :
                            (position === "keepers") ? "GK" :
                            (position === "defenders") ? "DEF" :
                            (position === "midfielders") ? "MF" :
                            (position === "attackers") ? "ATT" :
                            position;

                            return players?.map((player, playerIndex) => {
                                const { name, playerNumber } = player;
                                console.log(player)
                                return (
                                    <PlayerCard
                                        key={`${squadIndex}-${playerIndex}`}
                                        name={name}
                                        position={position} 
                                        teamLogo={`https://images.fotmob.com/image_resources/logo/teamlogo/${teamData.details?.id}.png`}
                                        image={`https://images.fotmob.com/image_resources/playerimages/${player.id}.png` || "https://shared-static-hosted4.stats.com/headshots/144x144/football/nfl/1116663.png"}
                                        playerNumber={playerNumber} 
                                        playerId={player.id}
                                        isInjured={player.isInjured}
                                    />
                                );
                            });
                        })}
                    </div>
                </div>
                <div className="flex flex-col gap-2">
                    <div className="text-sm text-[#6d6a7f]">Season Leaderboard</div>
                    <Standings responseTable={teamData || ""}/>
                </div>
                <div className="flex flex-col gap-2">
                    <div className="text-sm text-[#6d6a7f]">Team News</div>
                    <News teamId={id} colorTeam={teamData?.history?.teamColor}/>
                </div>
            </div>	
            }
		</>
	);
};

export default TeamDetail;
