import axios from "axios";

class ProvaApi {
    axiosClient = axios.create();

    makeRequest = async (url) => {
        try { 
            const timestamp = new Date().getTime(); 
            const fullUrl = `https://corsproxy.io/?https://www.fotmob.com/${url}&_=${timestamp}`;
            const response = await this.axiosClient({
                method: "GET",
                url: fullUrl,
            });    
            return response;
        } catch (error) {
            console.error(error);
            throw error;
        }
    };

    makeSearch = async (url) => {
        try { 
            const fullUrl = `https://corsproxy.io/?https://apigw.fotmob.com/${url}`;
            const response = await this.axiosClient({
                method: "GET",
                url: fullUrl,
                headers: {
                    'Cache-Control': 'no-cache',
                },
            });    
            return response;
        } catch (error) {
            // console.error(error);
            throw error;
        }
    };
}

export default ProvaApi;
