import React, { useState } from 'react';

function MatchPoll(props) {
    const pollData = props.data;
    const [selectedTeam, setSelectedTeam] = useState(null);

    const handleError = (event) => {
        event.target.src = "https://www.fotmob.com/_next/static/media/team_fallback.3ae01170.png";
    };

    const handleTeamClick = (teamName) => {
        setSelectedTeam(teamName);
    };

    return (
        <div className="w-full flex flex-col gap-3 p-4 rounded-lg bg-white brd">
            <div className="font-medium text-base text-center ">{pollData?.oddspoll?.Facts?.[0]?.DefaultLabel}</div>
            <div className="flex justify-between">
                <div className={`flex flex-col gap-2 hover:opacity-50 cursor-pointer text-center items-start ${selectedTeam === pollData?.oddspoll?.HomeTeam ? 'selected' : ''}`} onClick={() => handleTeamClick(pollData?.oddspoll?.HomeTeam)}>
                    <div className="flex items-center justify-center h-10 w-10 rounded-full bg-white brd">
                        <img className="h-6 w-6 object-contain" onError={handleError} src={`https://images.fotmob.com/image_resources/logo/teamlogo/${pollData?.oddspoll?.HomeTeamId}.png`} alt="teamLogo" />
                    </div>
                    <div className="text-sm">{pollData?.oddspoll?.HomeTeam}</div>
                </div>
                <div className={`flex flex-col gap-2 hover:opacity-50 cursor-pointer text-center items-center justify-center h-10 w-10 rounded-full bg-white brd ${selectedTeam === 'X' ? 'selected' : ''}`} onClick={() => handleTeamClick('X')}>
                    <div className="text-sm">X</div>
                </div>
                <div className={`flex flex-col gap-2 hover:opacity-50 cursor-pointer text-center items-end ${selectedTeam === pollData?.oddspoll?.AwayTeam ? 'selected' : ''}`} onClick={() => handleTeamClick(pollData?.oddspoll?.AwayTeam)}>
                    <div className="flex items-center justify-center h-10 w-10 rounded-full bg-white brd">
                        <img className="h-6 w-6 object-contain" onError={handleError} src={`https://images.fotmob.com/image_resources/logo/teamlogo/${pollData?.oddspoll?.AwayTeamId}.png`} alt="teamLogo" />
                    </div>
                    <div className="text-sm">{pollData?.oddspoll?.AwayTeam}</div>
                </div>
            </div>
        </div>
    );
}

export default MatchPoll;
