import { useState } from "react";
import { Link } from "react-router-dom"
import ProvaApi from "../../api/ProvaApi";

function TeamForm(props) {
    const teamsForm = props.matchData?.content?.matchFacts?.teamForm
    console.log(teamsForm)

    const apiProva = new ProvaApi();
    const [matchData, setMatchData] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const fetchplayer = async () => {
            try {
                const timestamp = new Date().getTime(); 
                const responseMatchData = await apiProva.makeRequest(`api/matchDetails?matchId=${id}&_=${timestamp}`);
                setMatchData(responseMatchData.data);  
                setIsLoading(false); 
            } catch (error) {
                console.log(error);
                setIsLoading(false);
            }
        };

        fetchplayer(); 

    }, [id]);

    return (
        <div className="w-full rounded-lg bg-white brd p-4 md:px-24">
            <div className="text-lg font-medium text-center mb-6">Teams Form</div>
            <div className="flex gap-3 justify-between">
                {teamsForm?.map((matches, teamIndex) => (
                    <>
                    <div key={teamIndex} className="flex flex-1 justify-center flex-col gap-8 items-center">
                        {matches?.map((match, matchIndex) => {
                            const matchNumbers = match.linkToMatch.match(/#(\d+)$/);
                            const numeriFinali = matchNumbers ? matchNumbers[1] : null;

                            return (
                                <div className="w-full">
                                    <Link to={"/match-details/" + numeriFinali} key={matchIndex}>
                                        <div className="flex gap-4 justify-center w-full gap-5">
                                            <div className="flex gap-3 items-center">
                                                <div className="text-sm hidden md:block flex-nowrap truncate">{match?.home?.name}</div>
                                                <img className="h-7 w-7 block md:hidden" src={`https://images.fotmob.com/image_resources/logo/teamlogo/${match?.home.id}.png`} alt="team1Logo" />
                                            </div>
                                            <div className={`flex w-14 text-center text-sm px-3 py-0.5 rounded-full text-white text-nowrap ${match?.result === -1 ? "bg-red-600" : match?.result === 0 ? "bg-gray-600" : "bg-green-600"}`}>{match?.score}</div>
                                            <div className="flex gap-3 items-center">
                                                <img className="h-7 w-7 block md:hidden" src={`https://images.fotmob.com/image_resources/logo/teamlogo/${match?.away.id}.png`} alt="team1Logo" />
                                                <div className="text-sm hidden md:block flex-nowrap truncate">{match?.away?.name}</div>
                                            </div>
                                        </div>
                                    </Link>
                                </div>
                            );
                        })}
                    </div>
                    {teamIndex !== teamsForm.length - 1 && <div className="brd05"></div>}
                    </>
                ))}
            </div>
        </div>
    )
} 

export default TeamForm
