import React, { useEffect, useState } from "react";
import StandingLine from "./StandingLine";
import ApiManager from "../../api/ApiManager"; 

function Standings() {
    const api = new ApiManager();
    const [standingData, setStandingData] = useState([]);
    const [leagueData, setLeagueData] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                // const responseStanding = await api.makeRequest("standings?league=135&season=2023");
                const fakeData = Array.from({ length: 10 }, (_, index) => ({
                    team: { logo: `https://media.api-sports.io/football/teams/${index + 33}.png`, name: `Team ${index + 1}` },
                    all: { win: index+2, draw: index, lose: index+5, played: index+10 },
                    points: index+4,
                    rank: index + 1,
                }));

                setStandingData(fakeData);
                setLeagueData({ name: "Fake League", logo: "https://media.api-sports.io/football/teams/33.png", flag: "https://media.api-sports.io/football/teams/3.png" });
            } catch (error) {
                console.log(error);
            }
        };

        fetchData(); 

    }, []);

    return (
        <>
            <div className="flex flex-col gap-2">
                <div className="flex justify-between px-4">
                    <div className="flex gap-5 items-center basis-6/12">
                        <div className="h-10 w-10 rounded-full flex items-center justify-center" style={{border: "0.05em solid #a19db3"}}>
                            <img className="h-7 object-fit" src={leagueData.flag} alt="leagueIcon" />
                        </div>
                        <div className="text-xl font-medium">{leagueData.name}</div>                    
                    </div>
                    <div className="flex items-center basis-5/12 text-end">
                        <div className="text-[#6d6a7f] text-sm basis-2/12 ">G</div>
                        <div className="text-[#6d6a7f] text-sm basis-2/12 ">V</div>
                        <div className="text-[#6d6a7f] text-sm basis-2/12 ">N</div>
                        <div className="text-[#6d6a7f] text-sm basis-2/12 ">P</div>
                        <div className="text-[#6d6a7f] text-sm basis-2/12 ">DR</div>
                        <div className="text-sm basis-2/12 font-medium">PT</div>
                    </div>
                </div>
                {standingData.map((team, index) => (
                    <StandingLine
                        key={index}
                        teamLogo={team.team.logo}
                        teamName={team.team.name}
                        teamWins={team.all.win}
                        teamDraws={team.all.draw}
                        teamLose={team.all.lose}
                        teamPoints={team.points}
                        teamPlayed={team.all.played}
                        number={team.rank}
                        teamId={team.team.id}
                    />
                ))}
            </div>
        </>
    )
}

export default Standings;
