function LeagueTransfers(props) {
    const data = props.data.data
    console.log(data)
    const handleError = (event) => {
        event.target.src = "https://i.ibb.co/q9SWXK2/player-placeholder.png";
    };

    const transfersToRender = !props.full ? data?.slice(0, 10) : data;

    return(
        <div className={`p-4 brd bg-white rounded-lg w-full`}>
            {transfersToRender?.map((transfer, index) => (
                <div className="flex flex-col gap-3" key={index}>
                    <div className="w-full py-3 flex flex-col justify-center">
                        <div className="overflow-hidden h-8 w-8 md:h-12 md:w-12 rounded-full relative">
                            <div className="overflow-hidden h-8 w-8 md:h-12 md:w-12 rounded-full">
                                </div>
                            <img className="h-full" alt="playerPhoto" onError={handleError} src={`https://images.fotmob.com/image_resources/playerimages/${transfer?.playerId}.png`} />
                        </div>
                        {transfer?.position?.label &&
                            <div key={index} className="flex px-2 py-0.5 bg-gray-200 brd absolute -bottom-2 rounded-full">
                                <div className="text-xs">{transfer?.position?.label}</div>
                            </div>
                        }
                    </div>
                    <div className="brd05"></div>
                </div>
            ))}
        </div>
    )
}

export default LeagueTransfers
