import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import ProvaApi from "../api/ProvaApi";
import Layout from "../components/Layout/Layout";
import LeagueDetailsTop from "../components/League/LeagueDetailsTop";

const Leagues = () => {
	const {id} = useParams();
    const apiProva = new ProvaApi();
    const [leagueData, setLeagueData] = useState([]);
	const [isLoading, setIsLoading] = useState(true);o

	useEffect(() => {
        const fetchLeague = async () => {
            try {
                const responseLeagueData = await apiProva.makeRequest(`api/leagues?id=${id}&ccode3=ITA`);
                setLeagueData(responseLeagueData.data);
                setIsLoading(false); 
            } catch (error) {
                console.log(error);
                setIsLoading(false);
            }
        };

        fetchLeague(); 

    }, [id]);

	eturn (
		<>
			<Layout />
			<div className="text-black flex flex-col p-4 gap-3">
                {isLoading ? (
                    <div role="status" className="animate-pulse md:flex md:items-center">
                        <div className="flex items-center justify-center w-full h-48 bg-gray-300 rounded-lg w-full"></div>
                    </div>
                ) : (
                    <LeagueDetailsTop data={leagueData} leagueId={id}/>
                )}
			</div>
		</>
	);
};

export default Leagues;
