import { Link } from "react-router-dom"

function TeamForm(props) {
    const teamsForm = props.matchData?.content?.matchFacts?.teamForm
    console.log(teamsForm)
    return (
        <div className="w-full rounded-lg bg-white brd p-4 md:px-24">
            <div className="text-lg font-medium text-center mb-6">Teams Form</div>
            <div className="flex gap-3 justify-between">
                {teamsForm?.map((matches, teamIndex) => (
                    <div key={teamIndex} className="flex flex-col gap-8 items-center">
                        {matches?.map((match, matchIndex) => {
                            const matchNumbers = match.linkToMatch.match(/#(\d+)$/);
                            const numeriFinali = matchNumbers ? matchNumbers[1] : null;

                            return (
                                <Link to={"/match-details/" + numeriFinali} key={matchIndex}>
                                    <div className="flex gap-4 items-center">
                                        <div className="flex gap-3 items-center">
                                            <div className="text-sm hidden md:block">{match?.home?.name}</div>
                                            <img className="h-7 w-7" src={`https://images.fotmob.com/image_resources/logo/teamlogo/${match?.home.id}.png`} alt="team1Logo" />
                                        </div>
                                        <div className={`flex flex-1 text-sm px-3 py-0.5 rounded-full text-white ${match?.result === -1 ? "bg-red-600" : match?.result === 0 ? "bg-gray-600" : "bg-green-600"}`}>{match?.score}</div>
                                        <div className="flex gap-3 items-center">
                                            <img className="h-7 w-7" src={`https://images.fotmob.com/image_resources/logo/teamlogo/${match?.away.id}.png`} alt="team1Logo" />
                                            <div className="text-sm hidden md:block">{match?.away?.name}</div>
                                        </div>
                                    </div>
                                </Link>
                            );
                        })}
                    </div>
                ))}
            </div>
        </div>
    )
} 

export default TeamForm
