import React, { useEffect, useState } from "react";
import StandingLine from "./StandingLine";
import ApiManager from "../../api/ApiManager"; 

function Standings(props) {
    const api = new ApiManager();
    const [teamData, setTeamData] = useState([]);

    useEffect(() => {
        const fetchStandings = async () => {
            try {
                const response = await api.makeRequest("competitions/PL/standings");
                setTeamData(response.response[0].league.standings[0]);
            } catch (error) {
                console.log(error);
            }
        };

        fetchStandings();
    }, []); 

    return (
        <>
            <div className="flex w-full items-center justify-between">
                <div className="flex items-center gap-4">
                    <img className="h-8" src={props.leagueIcon} alt="leagueIcon"/>
                    <div className="text-xl font-bold">{props.leagueName}</div>
                </div>
                <div>
                    <img className="h-6" src={props.leagueFlag} alt="leagueFlag"/>
                </div>
            </div>
            <div className="mt-7 flex flex-col gap-2">
                <div className="flex gap-4">
                    <div className="flex gap-5 items-center basis-6/12">
                        <div className="font-bold text-lg text-slate-400">Club</div>
                    </div>
                    <div className="flex items-center  basis-3/12">
                        <div className="text-lg basis-3/12 text-start">W</div>
                        <div className="text-lg basis-3/12 text-center">D</div>
                        <div className="text-lg basis-3/12 text-center">L</div>
                        <div className="text-lg basis-3/12 text-end">Points</div>
                    </div>
                    <div className="flex items-center basis-3/12 justify-end gap-2">
                        <div className="text-lg text-slate-400">Last match</div>
                    </div>
                </div>
                {teamData.map((team, index) => (
                    <StandingLine
                        key={index}
                        teamLogo={team.team.logo}
                        teamName={team.team.name}
                        teamWins={team.all.win}
                        teamDraws={team.all.draw}
                        teamLose={team.all.lose}
                        teamPoints={team.all.points}
                        teamStreak={team.form}
                        number={team.rank}
                    />
                ))}
            </div>
        </>
    )
}

export default Standings;
