import { Link } from "react-router-dom";

function LeagueTransfers(props) {
    const data = props.data.data
    console.log(data)
    const handleError = (event) => {
        event.target.src = "https://i.ibb.co/q9SWXK2/player-placeholder.png";
    };

    const transfersToRender = !props.full ? data?.slice(0, 10) : data;

    return(
        <div className={`p-4 brd bg-white rounded-lg w-full`}>
            {transfersToRender?.map((transfer, index) => (
                <div className="flex flex-col gap-3" key={index}>
                    <Link to={"/layer-details/" + (transfer?.playerId)}>
                        <div className="w-full py-3 flex flex-col justify-center items-center gap-4">
                            <div className="h-8 w-8 md:h-12 md:w-12 rounded-full relative">
                                <div className="overflow-hidden h-8 w-8 md:h-12 md:w-12 rounded-full">
                                    <img className="h-full" alt="playerPhoto" onError={handleError} src={`https://images.fotmob.com/image_resources/playerimages/${transfer?.playerId}.png`} />
                                </div>
                                {transfer?.position?.label &&
                                    <div key={index} className="flex px-2 py-0.5 bg-gray-200 brd absolute -bottom-3 -end-3 rounded-full">
                                        <div className="text-xs">{transfer?.position?.label}</div>
                                    </div>
                                }
                            </div>
                            <div className="text-ls font-medium">{transfer?.name}</div>
                            <div className="flex gap-3">
                                <div className="h-4 w-4">
                                    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 18 18"><path fill="none" d="M0 0h18v18H0z"></path><path data-name="Path 4273" d="M17.745-67.742a9.054 9.054 0 0 1-9-9 9.064 9.064 0 0 1 9.013-9 9.047 9.047 0 0 1 8.987 9 9.053 9.053 0 0 1-9 9zm5.02-9a.906.906 0 0 0-.313-.7l-3.3-3.245a.835.835 0 0 0-.592-.235.782.782 0 0 0-.8.8.813.813 0 0 0 .261.583l1.166 1.131 1.1.879-2-.087h-4.736a.825.825 0 0 0-.844.87.83.83 0 0 0 .844.87h4.733l2-.1-1.087.887-1.174 1.122a.785.785 0 0 0-.261.592.782.782 0 0 0 .8.8.874.874 0 0 0 .592-.235l3.3-3.245a.91.91 0 0 0 .31-.692z" className="fill-green-500"></path></svg>
                                </div>
                            </div>
                        </div>
                    </Link>
                    <div className="brd05"></div>
                </div>
            ))}
        </div>
    )
}

export default LeagueTransfers
