import { Link } from "react-router-dom";

function MatchCard(props) {
    const fixData = props.matchData
    const data = new Date(fixData?.status?.utcTime);
    const mese = ("0" + (data.getMonth() + 1)).slice(-2); 
    const giorno = ("0" + data.getDate()).slice(-2);
    const dataFormattata = `${mese}/${giorno}`;

    return (
        <div className="p-4 rounded-lg w-full bg-white brd">
            <Link to={"/match-details/" + fixData?.id}>
            <div className="flex justify-between">
                <div className="w-full flex flex-col text-center gap-2 items-start">
                    <Link to={"/team/" + (fixData?.home?.id)}>
                    <div className="flex flex-col items-center gap-2">
                        <img className="h-10 object-contain" src={`https://images.fotmob.com/image_resources/logo/teamlogo/${fixData?.home?.id}.png` || ""} alt="teamLogo" />
                        <div className="text-xs font-medium">{fixData?.home?.name}</div>
                    </div>
                    </Link>
                </div>
                <div className="w-full flex flex-col gap-2 items-center justify-center">
                    {!fixData?.status?.started && !fixData?.status?.finished ? (
                        <div className="text-pink-600 text-sm bg-pink-200 rounded-full px-3 py-0.5">
                            {dataFormattata}
                        </div>
                    ) : (
                        fixData?.notStarted === true ? (
                            <>
                                <div className="text-lg font-medium">{fixData?.status?.scoreStr}</div>
                                <div className="text-gray-600 text-sm bg-gray-200 rounded-full px-3 py-0.5">
                                    {fixData?.status?.reason?.short}
                                </div>
                            </>
                        ) : (
                            <>
                                <div className="text-white absolute top-0 text-sm start-0 bg-green-600 rounded-full px-3 py-0.5">
                                    {fixData?.status?.scoreStr}
                                </div>
                            </>
                        )
                    )}
                </div>
                <div className="w-full flex flex-col text-end gap-2 items-end">
                    <Link to={"/team/" + (fixData?.away?.id)}>
                        <div className="flex flex-col items-center gap-2">
                            <img className="h-10 object-contain" src={`https://images.fotmob.com/image_resources/logo/teamlogo/${fixData?.away?.id}.png` || ""} alt="teamLogo" />
                            <div className="text-xs font-medium">{fixData?.away?.name}</div>
                        </div>
                    </Link>
                </div>
            </div>

        </Link>
        </div>
    )
}

export default MatchCard
