import ApiManager from "./api/ApiManager";

const App = () => {
	const api = new ApiManager();
	const [matches, setMatches] = useState({});

	const matchesApi = async () => {
		try {
			const responseMatches = await api.makeRequest("standings", "GET", {season: 2023, league: 39});
			setMatches(responseMatches);
			console.log(matches);
		} catch (error) {
			console.log(error);
		}
	};

