			<div className="filmItem d-flex flex-column justify-content-center" key={index}>
				<FilmCont hoverable={props.hoverable} filmKey={filmKey} logo={filmData.logo} time={filmData.time} genres={filmData.genres} id={filmData.id} films={FilmList} urlVideo={filmData.video} isFavourite={filmData.isFavourite} isNet={filmData.isNet} isTop={filmData.isTop} recentAdded={filmData.recentAdded} urlImg={filmData.image} />
				{props.isContinueWatching && (
				<div className="d-flex justify-content-center">
