import { useParams } from "react-router-dom";
import React, { useEffect, useState } from "react";
import ApiManager from "../../api/ApiManager";

function PlayerCard(props) {
    const { id } = useParams();
    const api = new ApiManager();
    const [playerData, setPlayerData] = useState([]);
    const [playerTeamData, setPlayerTeamData] = useState([]);

    // useEffect(() => {
    //     const fetchPlayers = async () => {
    //         try {
    //             const response = await api.makeRequest(`players/squads?team=${id}`);
    //             setPlayerData(response.response[0].players);
    //             setPlayerTeamData(response.response[0].team);
    //             console.log(playerData);
    //         } catch (error) {
    //             console.log(error);
    //         }
    //     };
    //
    //     fetchPlayers();
    // }, [id]); 

    const fakePlayerData = playerData.length === 0
    ? [...Array(20)].map((_, index) => ({
        name: `Player ${index + 1}`,
        position: "Attacker",
        number: index + 1,
        photo: null, 
        }))
    : playerData;

    return (
        <div className="flex gap-3">
            {fakePlayerData.map((player, index) => (
                <div className="bg-gray-100 rounded-lg ps-3 pt-3 flex items-center" key={index}>
                    <div className="flex flex-col gap-3 pb-3">
                        <img className="h-10 w-10 object-contain" src={playerTeamData.logo || "https://media.api-sports.io/football/teams/85.png"} alt="Team Logo"></img>
                        <div className="leading-snug text-lg text-[#6d6a7f] break-normal">{player.name}</div>
                        <div className="flex">
                            <span className="bg-red-100 text-red-800 text-xs font-medium px-2.5 py-0.5 rounded border border-red-400">ATT</span>
                        </div>
                        <div className="leading-snug text-lg text-[#6d6a7f] font-medium">{player.number}</div>
                    </div>
                    <div className="w-max flex items-end">
                        <img className="h-48 object-contain object-bottom mix-blend-multiply rounded-lg" alt={`playerPhotoNumber${index}`} src={player.photo || "https://shared-static-hosted4.stats.com/headshots/144x144/football/nfl/1116663.png"}></img>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default PlayerCard;
