import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function StandingLine(props) {
    const [bgLine, setBgLine] = useState("#312e40");

    useEffect(() => {
        if (props.number <= 4) {
            setBgLine("champions");
        } else if (props.number === 5) {
            setBgLine("europa");
        } else if (props.number >= 18 && props.number <= 20) {
            setBgLine("retro");
        }
    }, [props.teamStreak, props.number]);

    return (
        <Link to={"/team/" + (props.teamId ?? "defaultTeamId")}>
            <div className={`bg-${bgLine} p-4 rounded-lg flex gap-4`}>
                <div className="flex gap-5 items-center basis-6/12">
                    <div className="font-bold text-sm">{props.number || "1"}</div>
                    <div className="w-1/4 h-1/4 border-solid border-1/8 border-[#a19db3] rounded-full box-content"></div>
                    <img className="h-8" src={props.teamLogo} alt="teamLogo" />
                    <div className="text-sm">{props.teamName ?? "Inter"}</div>
                </div>
                <div className="flex items-center basis-6/12 text-end">
                    <div className="text-sm text-[#6d6a7f] basis-2/12">{props.teamPlayed}</div>
                    <div className="text-sm text-[#6d6a7f] basis-2/12">{props.teamWins}</div>
                    <div className="text-sm text-[#6d6a7f] basis-2/12">{props.teamDraws}</div>
                    <div className="text-sm text-[#6d6a7f] basis-2/12">{props.teamLose}</div>
                    <div className="text-sm text-[#6d6a7f] basis-2/12">{props.teamLose}</div>
                    <div className="text-sm font-bold basis-2/12">{props.teamPoints}</div>
                </div>
            </div>
        </Link>
    );
}

export default StandingLine;
