import React, { useEffect, useState } from "react";
import ApiManager from "./api/ApiManager";
import StandingLine from "./components/standings/StandingLine";

const App = () => {
	const api = new ApiManager();
	const [matches, setMatches] = useState({});

	const matchesApi = async () => {
		try {
			const responseMatches = await api.makeRequest("standings", "GET", {season: 2023, league: 39});
			setMatches(responseMatches);
			console.log(matches);
		} catch (error) {
			console.log(error);
		}
	};

	useEffect(() => {
		matchesApi();
	}, []);

	const leagueIcon = matches.response?.[0]?.league?.logo
	const leagueName = matches.response?.[0]?.league?.name


	return (
		<>
		<div className="main p-4 flex">
			<div className="text-white text-white flex flex-col p-4 w-full">
				<div className="flex w-full items-center justify-between">
					<div className="flex items-center gap-4">
						<img className="h-8" src="https://media.api-sports.io/football/leagues/39.png" alt="leagueIcon"/>
						<div className="text-xl font-bold">Premier League</div>
					</div>
					<div>
						<img className="h-6" src="https://media.api-sports.io/flags/gb.svg" alt="leagueFlag"/>
					</div>
				</div>
				<div className="mt-7 flex flex-col gap-2">
					<div className="flex gap-4">
						<div className="flex gap-5 items-center basis-6/12">
							<div className="font-bold text-lg text-slate-400">Club</div>
						</div>
						<div className="flex items-center  basis-3/12">
							<div className="text-lg basis-3/12 text-start">W</div>
							<div className="text-lg basis-3/12 text-center">D</div>
							<div className="text-lg basis-3/12 text-center">L</div>
							<div className="text-lg basis-3/12 text-end">Points</div>
						</div>
						<div className="flex items-center basis-3/12 justify-end gap-2">
							<div className="text-lg text-slate-400">Last match</div>
						</div>
					</div>
					<StandingLine teamLogo="https://media.api-sports.io/football/teams/50.png" teamName="Cacca Merda" teamWins="13" teamDraws="1" teamLose="3" teamPoints="35" teamStreak="WW"/>
				</div>
			</div>
		</div>
		</>
	);
};

export default App;
