import React, { useState, useEffect } from "react";

function StandingLine(props) {
    const [streak, setStreak] = useState(props.teamStreak);
    const [bgLine, setBgLine] = useState("#312e40");

    useEffect(() => {
        setStreak(props.teamStreak);

        if (props.number <= 4) {
            setBgLine("purple-600");
        } else {
            setBgLine("orange-300");
        }
    }, [props.teamStreak, props.number]);

    return (
        <div className={`bg-${bgLine} bg-opacity-50 p-4 rounded-lg flex gap-4`}>
            <div className="flex gap-5 items-center basis-6/12">
                <div className="font-bold text-lg">{props.number}</div>
                <img className="h-8" src={props.teamLogo} alt="teamLogo"/>
                <div className="text-lg">{props.teamName}</div>
            </div>
            <div className="flex items-center basis-6/12">
                <div className="text-lg basis-3/12 text-start">{props.teamWins}</div>
                <div className="text-lg basis-3/12 text-center">{props.teamDraws}</div>
                <div className="text-lg basis-3/12 text-center">{props.teamLose}</div>
                <div className="text-lg basis-3/12 text-end">{props.teamPoints}</div>
            </div>
        </div>
    );
}

export default StandingLine;
