import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import ProvaApi from "../api/ProvaApi";
import Layout from "../components/Layout/Layout";
import Standings from "../components/Standings/Standing";

const LeagueDetail = () => {
	const {id} = useParams();
    const apiProva = new ProvaApi();
    const [teamData, setTeamData] = useState([]);

    useEffect(() => {
        const fetchTeam = async () => {
            try {
                const responseTeamData = await apiProva.makeRequest(`https://www.fotmob.com/api/leagues?id=${id}&ccode3=ITA`);
                setTeamData(responseTeamData);  
                console.log(teamData)     
            } catch (error) {
                console.log(error);
            }
        };

        fetchTeam(); 

    }, [id]);

	return (
		<>
			<Layout />
			<div className="text-black flex flex-col p-4 gap-3">
				<div className="flex flex-col md:flex-row gap-3 w-100">	
					<div className="w-100">
						<Standings id={id} />
					</div>
				</div>
			</div>
		</>
	);
};

export default LeagueDetail;
