import React, { useEffect, useState } from "react";
import ApiManager from "./api/ApiManager";

const App = () => {
	const api = new ApiManager();
	const [matches, setMatches] = useState({});

	const matchesApi = async () => {
		try {
			const responseMatches = await api.makeRequest("standings", "GET", {season: 2023, league: 39});
			setMatches(responseMatches);
			console.log(matches);
		} catch (error) {
			console.log(error);
		}
	};

	useEffect(() => {
		matchesApi();
	}, []);

	const leagueIcon = matches.response?.[0]?.league?.logo
	const leagueName = matches.response?.[0]?.league?.name 

	return (
		<>
		<div className="bg-midnight main p-4 flex items-center justify-center flex-col">
			<div className="bg-slate-50	text-black text-white flex p-4 rounded-xl w-full">
				<div className="flex w-full items-center justify-between">
					<div className="flex items-center gap-4">
						<img className="h-8" src="https://media.api-sports.io/football/leagues/39.png" alt="leagueIcon"/>
						<div className="text-xl">Premier League</div>
					</div>
					<div>
						<img className="h-6" src="https://media.api-sports.io/flags/gb.svg" alt="leagueFlag"/>
					</div>
				</div>
				<div className="flex flex-col gap-4">
					<div className="flex justify-center">

					</div>
				</div>
			</div>
		</div>
		</>
	);
};

export default App;
