function PlayerMatchStats(props) {
    const playerMatchStat = props.data
    console.log(playerMatchStat)

    return(
        <div className="flex flex-col bg-white brd rounded-lg py-4 gap-3">
            <div className="flex gap-3 items-center px-4">
                <div className="text-lg font-medium">Match Stats</div>
            </div>
            <div className="brd"></div>
            <div className="flex justify-between px-4 items-center">
                <div className="text-base text-[#6d6a7f]">Matches</div>
                <div className="flex gap-4">
                    <img alt="icon" className="h-4" src="https://img.icons8.com/?size=512&id=118085&format=png"></img>
                    <img alt="icon" className="h-4" src="https://i.ibb.co/9tyd46p/SVG-to.png"></img>
                    <img alt="icon" className="h-4" src="https://img.icons8.com/?size=512&id=110622&format=png"></img>
                    <img alt="icon" className="h-4" src="https://img.icons8.com/?size=512&id=85185&format=png"></img>
                </div>
            </div>
            <div className="brd"></div>
            <div className="flex flex-col px-4 gap-4">
            {playerMatchStat?.slice(0, 10).map((matchStat, index) => {
                const matchDate = new Date(matchStat?.matchDate?.utcTime);
                const options = { month: 'short', day: 'numeric', year: 'numeric' };
                const formattedDate = new Intl.DateTimeFormat('en-US', options).format(matchDate);

                const currentDate = new Date();
                const yesterday = new Date(currentDate);
                yesterday.setDate(currentDate.getDate() - 1);

                return (
                    <div key={index} className="flex text-center justify-between">
                        <div className="flex gap-3 items-center basis-3/12">
                            <img alt="leagueIcon" className="h-4" src={`https://images.fotmob.com/image_resources/logo/leaguelogo/${matchStat?.leagueId}.png`} ></img>
                            <div className="text-base truncate">{currentDate.toDateString() === matchDate.toDateString() ? 'Today' : yesterday.toDateString() === matchDate.toDateString() ? 'Yesterday' : formattedDate}</div>
                        </div>
                        <div className="flex justify-between basis-4/12">
                            <div className="flex gap-3">
                                <img alt="teamIcon" className="h-4" src={`https://images.fotmob.com/image_resources/logo/teamlogo/${matchStat?.leagueId}.png`} ></img>
                                <div className="text-base truncate">{matchStat?.opponentTeamName}</div>
                            </div>
                        </div>
                    </div>
                );
            })}
            </div>
        </div>
    )
}

export default PlayerMatchStats
