import axios from "axios";

class ApiManager {
    axiosClient = axios.create();
    apiKey = process.env.REACT_APP_API_KEY;

    // async initialize() {   
        /////// BEARER TOKEN ///////
        // const bearerToken = process.env.REACT_APP_API_KEY
        // this.axiosClient.defaults.headers = {
        //     "Content-Type": "application/json",
        //     Accept: "application/json",
        //     'X-RapidAPI-Key': bearerToken,
        //     'X-RapidAPI-Host': 'api-football-v1.p.rapidapi.com'
        // };  
    // } 

    async initialize() {
        this.axiosClient.defaults.baseURL = process.env.REACT_APP_BASE_URL;
        this.axiosClient.defaults.headers = {
            'X-RapidAPI-Key': this.apiKey,
            'X-RapidAPI-Host': 'api-football-v1.p.rapidapi.com'
        };
    }

    async makeRequest(options) {
        await this.initialize();
        try {
            const response = await this.axiosClient.request(options);
            return response.data;
        } catch (error) {
            console.error(error);
            throw error;
        }
    }

    get = async (url, params) => {
        const options = {
            method: 'GET',
            url: url,
            params: params,
        };
        return this.makeRequest(options);
    }
}

const options = {
    method: 'GET',
    url: 'https://api-football-v1.p.rapidapi.com/v3/timezone',
    headers: {
        'X-RapidAPI-Key': 'bf6ef26cf2msh79dcd277aa4fbc6p172f50jsn2543ec644b80',
        'X-RapidAPI-Host': 'api-football-v1.p.rapidapi.com'
    }
};

try {
    const response = await axios.request(options);
    console.log(response.data);
} catch (error) {
    console.error(error);
}

export default ApiManager;
