import { Link } from "react-router-dom"

function Statistics(props) {
    const data = props.data;
    console.log(data);
    const handleError = (event) => {
        event.target.src = "https://i.ibb.co/q9SWXK2/player-placeholder.png";
    };

    return (
        <div className="flex md:grid-cols-3 gap-5">
            {data?.map((stat, index) => (
                <div key={index} className="flex md:basis-4/12 py-4 gap-3 rounded-lg bg-white brd flex-col w-full">
                    <div className="text-lg font-medium px-4">{stat.header}</div>
                    <div className="brd"></div>
                    <div className="flex flex-col gap-3 px-4">
                        {stat?.topThree?.map((pl, index2) => (
                            <Link to={"/player/" + (pl?.id)} key={index2}>
                                <div className="flex justify-between items-center">
                                    <div className="flex items-center gap-3">
                                        <div className="overflow-hidden h-12 w-12 rounded-full">
                                            <img className="h-full" alt="playerPhoto" onError={handleError} src={`https://images.fotmob.com/image_resources/playerimages/${pl?.id}.png`} />
                                        </div>
                                        <div className="text-base">{pl?.name}</div>
                                    </div>
                                    <div className="text-lg font-medium h-12 w-12 flex items-center justify-center bg-gray-100 rounded-full">
                                        {pl?.value}
                                    </div>
                                </div>
                            </Link>
                        ))}
                    </div>
                </div>
            ))}
        </div>
    );
}

export default Statistics;
