import { Link } from "react-router-dom";

function MatchLine(props) {
    const matchData = props.matchData

    let dateString = matchData?.time
    const dateObject = new Date(dateString);

    const formattedTime = dateObject.toLocaleTimeString("en-US", {
      hour: "numeric",
      minute: "numeric",
      hour12: true
    });

    return (
        <>
        <Link to={"/match-details/" + (matchData.id)}>
            <div className="flex items-center ">
                <div className="flex gap-5 items-center justify-start">
                    <div className="flex text-center justify-center">
                        {matchData.status.finished && matchData.status.cancelled ? (
                            <span className="text-nowrap bg-red-100 text-red-800 text-xs font-medium px-2.5 py-0.5 rounded border border-red-400">
                                {matchData.status.reason.short}
                            </span>
                        ) : matchData.status.finished ? (
                            <span className="text-nowrap bg-orange-100 text-orange-800 text-xs font-medium px-2.5 py-0.5 rounded border border-orange-400">
                                {matchData.status?.scoreStr}
                            </span>
                        ) : (
                            <>
                                {matchData.status.ongoing ? (
                                    <div className="flex flex-col gap-3 items-center">
                                        <span className="h-fit w-full text-nowrap bg-green-100 text-green-800 text-xs font-medium px-2.5 py-0.5 rounded border border-green-400">
                                            {matchData.status.liveTime.short}
                                        </span>
                                        <span className="h-fit text-nowrap bg-gray-100 text-gray-800 text-xs font-medium px-2.5 py-0.5 rounded border border-gray-400">
                                            {matchData.status.scoreStr}
                                        </span>
                                    </div>
                                ) : (
                                    <span className="text-nowrap bg-purple-100 text-purple-800 text-xs font-medium px-2.5 py-0.5 rounded border border-purple-400">
                                        {formattedTime}
                                    </span>
                                )}
                            </>
                        )}
                    </div>
                    <div className="flex flex-col w-100 gap-2">
                        <div className="flex gap-3 items-center">
                            <Link to={"/team/" + (matchData.home.id)}>
                                <img className="h-6 w-6 object-contain" src={`https://images.fotmob.com/image_resources/logo/teamlogo/${matchData.home.id}.png`} alt="teamLogo" />
                            </Link>
                            <div className="font-base">{matchData.home.name}</div>
                        </div>
                        <div className="flex gap-3 items-center ">
                            <Link to={"/team/" + (matchData.away.id)}>
                                <img className="h-6 w-6 object-contain" src={`https://images.fotmob.com/image_resources/logo/teamlogo/${matchData.away.id}.png`} alt="teamLogo" />
                            </Link>
                            <div className="font-base">{matchData.away.name}</div>
                        </div>            
                    </div>
                </div>
            </div>
        </Link>
        </>
    )
}

export default MatchLine
