import React, { useEffect, useState } from "react";
import ApiManager from "./api/ApiManager";
import Standings from "./components/standings/Standings";

const App = () => {
	const api = new ApiManager();
	const [leagueIcon, setLeagueIcon] = useState("");
	const [leagueName, setLeagueName] = useState("");

	const matchesApi = async () => {
		try {
			const responseMatches = await api.makeRequest("competitions/PL/standings");
			console.log(responseMatches)
			const icon = responseMatches.response?.[0]?.league?.logo;
			const name = responseMatches.response?.[0]?.league?.name;
			setLeagueIcon(icon);
			setLeagueName(name);
		} catch (error) {
			console.log(error);
		}
	};

	useEffect(() => {
		matchesApi();
	}, []);

	return (
		<>
			<div className="main p-4 flex">
				<div className="text-white text-white flex flex-col p-4 w-full">
					<Standings leagueIcon={leagueIcon} leagueName={leagueName} leagueFlag="https://media.api-sports.io/flags/gb.svg" />
				</div>
			</div>
		</>
	);
};

export default App;
