function TeamForm(props) {
    const teamsForm = props.matchData?.content?.matchFacts?.teamForm
    console.log(teamsForm)
    return (
        <div className="w-full rounded-lg bg-gray-100 p-4">
            <div className="text-lg font-medium text-center">Top Stats</div>
            <div className="flex gap-3 justify-between">
                {teamsForm?.map((matches, index) => (
                    <div key={index} className="flex flex-col gap-8 items-center">
                        {matches?.map((match, index) => (
                            <div key={index} className="flex gap-4 items-center">
                                <div className="flex gap-3">
                                <img className="h-7 w-7" src={`https://images.fotmob.com/image_resources/logo/teamlogo/${match?.home.id}.png`} alt="team1Logo"></img>
                                </div>
                                <div className={`text-sm px-3 py-0.5 rounded-full text-white ${ match?.result === -1 ? "bg-red-600" : match?.result === 0? "bg-gray-600": "bg-green-600"}`}>{match?.score}</div>
                                <img className="h-7 w-7" src={`https://images.fotmob.com/image_resources/logo/teamlogo/${match?.away.id}.png`} alt="team1Logo"></img>
                            </div>
                        ))}
                    </div>
                ))}
            </div>
        </div>
    )
} 

export default TeamForm
