import axios from "axios";

class ApiManager {
    axiosClient = axios.create();
    apiKey = process.env.REACT_APP_API_KEY;

    async initialize() {        

        /////// BEARER TOKEN ///////
        // const bearerToken = process.env.REACT_APP_API_KEY
        // this.axiosClient.defaults.headers = {
        //     "Content-Type": "application/json",
        //     Accept: "application/json",
        //     'X-RapidAPI-Key': bearerToken,
        //     'X-RapidAPI-Host': 'api-football-v1.p.rapidapi.com'
        // };

        this.axiosClient.defaults.baseURL = process.env.REACT_APP_BASE_URL;
        this.axiosClient.defaults.headers = process.env.REACT_APP_HEADERS
    } 

    get = async(url, url2="") => {
        await this.initialize();
        return this.axiosClient.get(`${url}&api_key=${this.apiKey}${url2}`).then(response => response);
    }

    post = async(url, payload) => {
        await this.initialize();
        return this.axiosClient.post(`${url}?api_key=${this.apiKey}`, payload)
        .then(response => response);
    }
}

export default ApiManager
