import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function MatchDetailsTop(props) {
    const matchData = props.matchData
    console.log(matchData)

    const inputDateString = "Fri, Jan 12, 2024, 19:45 UTC";
    const dateObject = new Date(inputDateString);
    const timeFormatted = dateObject.toLocaleTimeString("en-US", { hour: "numeric", minute: "numeric", hour12: true });
    const today = new Date().toLocaleDateString("en-US", { month: "2-digit", day: "2-digit" });
    const [dateFormatted, setDateFormatted] = useState("");
    useEffect(() => {
        if (dateObject.toDateString() === new Date().toDateString()) {
            setDateFormatted("Today");
        } else {
            setDateFormatted(dateObject.toLocaleDateString("en-US", { month: "2-digit", day: "2-digit" }));
        }
    }, []);

    const date = new Date(matchData?.general?.matchTimeUTC);
        const options = { 
        year: "numeric", 
        month: "long", 
        day: "numeric", 
        hour: "numeric", 
        minute: "numeric", 
        // timeZoneName: "short" 
    };
    const matchDateFull = date.toLocaleDateString("en-US", options);

    const [gameTime, setGameTime] = useState('35:42');

  useEffect(() => {
    const intervalId = setInterval(() => {
      // Decomponi la stringa per ottenere minuti e secondi
      const [minutes, seconds] = gameTime.split(':').map(Number);

      // Calcola il nuovo tempo di gioco aggiungendo un secondo
      const newSeconds = (seconds + 1) % 60;
      const newMinutes = minutes + Math.floor((seconds + 1) / 60);

      // Formatta il nuovo tempo di gioco come stringa
      const newTime = `${String(newMinutes).padStart(2, '0')}:${String(newSeconds).padStart(2, '0')}`;

      // Aggiorna lo stato con il nuovo tempo di gioco
      setGameTime(newTime);
    }, 1000); // Chiamare ogni secondo (1000 millisecondi)

    // Pulisce l'intervallo quando il componente viene smontato
    return () => clearInterval(intervalId);
  }, [gameTime]);


    return (
        <div className="min-w-full rounded-lg bg-gray-100 flex flex-col overflow-hidden">
            <div className="pt-4">
                <div className="flex justify-center gap-3 items-center pb-4">
                    <img alt="matchLeagueIcon" className="h-8" src={`https://images.fotmob.com/image_resources/logo/leaguelogo/${matchData?.general?.parentLeagueId}.png`}></img>
                    <div className="text-base">{matchData?.general?.leagueName}</div>
                </div>
                <div className="border"></div>
            </div>
            <div className="flex p-4 justify-between md:justify-center items-center gap-2 md:gap-24">
                <Link to={"/team/" + matchData?.header?.teams?.[0]?.id}>
                    <div className="flex flex-1 gap-3 items-center justify-end">
                        <div className="text-lg hidden md:block">{matchData?.header?.teams?.[0]?.name}</div>
                        <img className="h-14 md:h-8 object-contain" src={`https://images.fotmob.com/image_resources/logo/teamlogo/${matchData?.header?.teams?.[0]?.id}.png`} alt="playerTeamLogo" />
                    </div>
                </Link>
                <div className="flex flex-2 justify-center items-center">
                    {matchData?.header?.status?.scoreStr ? (
                        <div className="flex flex-col text-center">
                            <div className="text-3xl font-medium">{matchData?.header?.status?.scoreStr}</div>
                            <div className="text-lg text-green-600">{matchData?.header?.status?.liveTime?.long}</div>
                        </div>
                        ) : (
                            <>
                            <div className="flex flex-col gap-1 text-center">
                                <div className="text-3xl font-medium">{timeFormatted || ""}</div>
                                <div className="text-xl font-medium text-[#6d6a7f]">{dateFormatted || ""}</div>
                            </div>
                            </>
                    )}                
                </div>
                <Link to={"/team/" + matchData?.header?.teams?.[1]?.id}>
                    <div className="flex flex-1 gap-3 items-center justify-start ">
                        <img className="h-14 md:h-8 object-contain " src={`https://images.fotmob.com/image_resources/logo/teamlogo/${matchData?.header?.teams?.[1]?.id}.png`} alt="playerTeamLogo" />
                        <div className="text-lg hidden md:block">{matchData?.header?.teams?.[1]?.name}</div>
                    </div>
                </Link>
            </div>
            {matchData.header?.status?.started &&
                <div className="flex items-top justify-center px-4 py-2 gap-5">
                    <div className="flex-1">
                        {Object.entries(matchData?.header?.events?.homeTeamGoals || {}).map(([playerName, goals], index) => {
                            if (goals.length > 0) {
                                console.log(goals)
                                return (
                                    <div key={index} className="text-base text-[#6d6a7f] text-end">
                                        <Link to={"/player/" + goals[0].playerId}>
                                            <div>
                                                {playerName}
                                                {goals.map((goal, goalIndex) => (
                                                    <span className="font-medium ms-2" key={goalIndex}>
                                                        {goal.timeStr}' 
                                                        {goalIndex < goals.length - 1 && ", "}
                                                    </span>
                                                ))}
                                            </div>
                                        </Link>
                                    </div>
                                );
                            }
                            return null;
                        })}
                    </div>
                    <img className="h-4 mt-1" alt="goal" src="https://i.ibb.co/9tyd46p/SVG-to.png"></img>
                    <div className="flex-1">
                        {Object.entries(matchData?.header?.events?.awayTeamGoals || {}).map(([playerName, goals], index) => {
                            if (goals.length > 0) {
                                return (
                                    <div key={index} className="text-base text-[#6d6a7f] text-start">
                                        <Link to={"/player/" + goals[0].playerId}>
                                            <div>
                                                {goals.map((goal, goalIndex) => (
                                                    <span className="font-medium me-2" key={goalIndex}>
                                                        {goal.timeStr}' 
                                                        {goalIndex < goals.length - 1 && ", "}
                                                    </span>
                                                ))}
                                                {playerName}
                                            </div>
                                        </Link>
                                    </div>
                                );
                            }
                            return null;
                        })}
                    </div>
                </div>
             }
            <div className="">
                <div className="border"></div>
                <div className="flex flex-col md:flex-row justify-start md:justify-center gap-2 md:gap-5 items-start md:items-center p-4">
                    <div className="flex gap-2 items-center">
                        <img alt="timeIcon" className="h-4" src="https://img.icons8.com/?size=512&id=85506&format=png" />
                        <div className="text-sm text-[#6d6a7f]">{matchDateFull}</div>
                    </div>
                    <div className="flex gap-2 items-center">
                        <img alt="stadiumIcon" className="h-4" src="https://img.icons8.com/?size=512&id=uZRLE51QnN2j&format=png" />
                        <div className="text-sm text-[#6d6a7f]">{matchData?.content?.matchFacts?.infoBox?.Stadium?.name}</div>
                    </div>
                    <div className="flex gap-2 items-center">
                        <img alt="whistleIcon" className="h-4" src="https://img.icons8.com/?size=512&id=uZ9JzCeXJmTf&format=png" />
                        <div className="text-sm text-[#6d6a7f]">{matchData?.content?.matchFacts?.infoBox?.Referee?.text}</div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default MatchDetailsTop
