import MatchesCont from "../components/matches/MatchesCont";
import React, { useEffect, useState } from "react";
import ProvaApi from "../api/ProvaApi";
import Layout from "../components/Layout/Layout";
import Skeleton from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css'

const Matches = () => {
	const apiProva = new ProvaApi();
    const [matchData, setMatchData] = useState([]);
    const today = new Date();
    const year = today.getFullYear();
    const month = String(today.getMonth() + 1).padStart(2, '0');
    const day = String(today.getDate()).padStart(2, '0');
    const formattedDate = `${year}${month}${day}`;

    useEffect(() => {
        const fetchTransfer = async () => {
            try {
                const responsematchData = await apiProva.makeRequest(`api/matches?date=${formattedDate}&timezone=Europe%2FRome&ccode3=ITA`);
                setMatchData(responsematchData.data.leagues);
                console.log(responsematchData.data.leagues);
            } catch (error) {
                console.log(error);
            }
        };

        fetchTransfer(); 

    }, []);

	return (
		<>
			<Layout />
			<div className="text-black flex flex-col p-4 gap-6">
                <div className="flex flex-col rounded-lg bg-white brd p-4 gap-3">
                    <div className="flex justify-between">
                        <div className="h-6 w-6 rounded-full bg-gray-200 brd flex items-center justify-center">
                            img clas
                        </div>
                        <div className="text-lg font-medium">Today</div>
                        <div className="h-6 w-6 rounded-full bg-gray-200 brd"></div>
                    </div>
                    <div className="brd"></div>
                </div>
                {matchData ?
                <>
                    {matchData?.map((match, index) => (
                        <MatchesCont matchData={match.matches} key={index} leagueCountry={match.ccode} leagueTitle={match.name} leagueIcon={`https://images.fotmob.com/image_resources/logo/leaguelogo/${match?.primaryId}.png`}/>
                    ))}
                </>
                : <Skeleton />}
			</div>
		</>
	);
};

export default Matches;
