import axios from "axios";

class ProvaApi {
    axiosClient = axios.create();

    makeRequest = async (url) => {
        try { 
            const fullUrl = `https://www.fotmob.com/api/${url}`;
            const response = await this.axiosClient({
                method: "GET",
                url: fullUrl,
            });    
            console.log(response.data[0].data.legend[0].color);
            return response.data;
        } catch (error) {
            console.error(error);
            throw error;
        }
    };
}

export default ProvaApi;
