import React, { useEffect, useState } from "react";
import ApiManager from "./api/ApiManager";

const App = () => {
	const api = new ApiManager();
	const [matches, setMatches] = useState({});

	const matchesApi = async () => {
		try {
			const responseMatches = await api.makeRequest("standings", "GET", {season: 2023, league: 39});
			setMatches(responseMatches);
			console.log(matches);
		} catch (error) {
			console.log(error);
		}
	};

	useEffect(() => {
		matchesApi();
	}, []);

	const leagueIcon = matches.response[0]?.league.logo
	const leagueName = matches.response[0].league.anem

	return (
		<>
		<div className="main">
			<div className="standingCard">
				<div style={{display:"flex", alignItems:"center", gap:".5rem"}}>
					<img src={leagueIcon} style={{height:"2rem"}} alt="leagueIcon"/>
					<div className="leagueTitle">{leagueName}</div>
				</div>
				<div style={{display:"flex", gap:".3rem", flexDirection:"column"}}>
					<div style={{display:"flex", justifyContent:"center"}}>

					</div>
				</div>
			</div>
		</div>
		</>
	);
};

export default App;
