import ApiManager from "../../api/ApiManager";
import React, { useEffect, useState } from "react";

function TransferCard(props) {

    return (
        <div className="p-4 rounded-lg w-full bg-gray-100 flex flex-col gap-4">
            <div className="text-base font-medium text-center leading-snug">{transferData.player?.name ?? "Hernán Darío Burbano"}</div>
            <div className="flex justify-between">
                <div className="flex flex-col text-center gap-2">
                    <img className="h-10 object-fit" src={transferData.transfers?.[0].teams.out.logo ?? "https://media.api-sports.io/football/teams/42.png"} alt="teamLogo" />
                    <div className="text-xs font-medium text-[#6d6a7f]">{transferData.transfers?.[0].teams.out.name ?? "Inter"}</div>
                </div>
                <div className="flex flex-col gap-2 justify-center items-center">
                    {transferData.transfers?.[0].type === "Free" ? (
                        <span className="bg-green-100 text-green-800 text-xs font-medium px-2.5 py-0.5 rounded border border-green-400">{transferData.transfers?.[0].type}</span>
                    ) :
                        <span className="bg-green-100 text-green-800 text-xs font-medium px-2.5 py-0.5 rounded border border-green-400">Free</span>
                    }
                    {transferData.transfers?.[0].type === "Free" ? (
                        <span className="bg-lime-100 text-lime-800 text-xs font-medium px-2.5 py-0.5 rounded border border-lime-400">{transferData.transfers?.[0].date}</span>
                    ) :
                        <span className="bg-lime-100 text-lime-800 text-xs font-medium px-2.5 py-0.5 rounded border border-lime-400">2019-07-15</span>
                    }
                </div>
                <div className="flex flex-col text-center gap-2">
                    <img className="h-10 object-fit" src={transferData.transfers?.[0].teams.out.logo ?? "https://media.api-sports.io/football/teams/42.png"} alt="teamLogo" />
                    <div className="text-xs font-medium text-[#6d6a7f]">{transferData.transfers?.[0].teams.out.name ?? "Inter"}</div>
                </div>
            </div>
        </div>
    );
}

export default TransferCard;
