import { Link } from "react-router-dom";

function PlayerLineUp(props) {
    const player = props.player
    return (
        <Link to={"/player/" + (player?.id)}>
            <div className="flex flex-col items-center gap-1 md:gap-3 relative">
                <div className="h-9 w-9 bg-gray-100 rounded-full flex items-end justify-center relative">
                    {player?.rating?.num && 
                        <div style={{ backgroundColor: `${player.rating.bgcolor}` }} className="flex px-2 py-0.5 absolute -top-2 -end-4 text-xs items-center justify-end rounded-full">
                            {player?.rating?.isTop?.isTopRating && 
                            <img alt="MOTM"></img>
                            }
                            {player?.rating?.num}
                        </div>
                    }
                    {player?.events?.g !== undefined && Array.from({ length: player.events.g }).map((_, index) => (
                        <div key={index} className="flex w-5 h-5 bg-white absolute -bottom-2 items-center justify-center rounded-full"
                            style={{ left: `${20 + index * 10}px` }}>
                            <img src="https://i.ibb.co/9tyd46p/SVG-to.png" alt={`goalIcon-${index}`} className="w-3 h-3" />
                        </div>
                    ))}
                    {player?.events?.as !== undefined && Array.from({ length: player.events.as }).map((_, index) => (
                        <div key={index} className="flex w-5 h-5 bg-white absolute -bottom-2 items-center justify-center rounded-full"
                            style={{ right: `${20 + index * 10}px` }}>
                            <img src="https://img.icons8.com/?size=512&id=110622&format=png" alt={`assistIcon-${index}`} className="w-4 h-4" />
                        </div>
                    ))}
                    <div className="overflow-hidden h-8 w-8">
                        <img className="h-full" alt="playerPhoto" src={`https://images.fotmob.com/image_resources/playerimages/${player?.id}.png`} />
                    </div>
                </div>
                <div className="text-sm text-gray-100 text-center">
                    <span className="font-medium">{player?.shirt}</span> {player?.name?.lastName}
                </div>
                {player?.isCaptain && 
                    <div className="flex absolute -bottom-6 text-sm font-extrabold bg-white w-5 h-5 items-center justify-center rounded-full">
                        C
                    </div>
                }
            </div>
        </Link>
    )
}

export default PlayerLineUp
