    makeRequest = async (url) => {
        try { 
            const response = await this.axiosClient({
                method: "GET",
                url: `https://www.fotmob.com/api/${url}`,
            });    
            console.log(response.data);
            return response.data;
        } catch (error) {
            console.error(error);
            throw error;
        }
    };

}

export default ProvaApi;
