import { useState } from "react";
import MatchCard from "../matches/MatchCard";
import Standings from "../standings/Standing"

function LeagueDetailsTop(props) {    
    const leagueData = props?.data;
    console.log(leagueData)
    const initialTab = leagueData?.tabs?.[0] || "overview";
    const [activeTab, setActiveTab] = useState(initialTab); 

    const handleTabClick = (tab) => {
        setActiveTab(tab);
    };

    return(
        <div className="flex flex-col gap-3">
            <div className="flex flex-col px-4 pt-4 bg-white brd rounded-lg gap-6 md:gap-12">
                <div className="flex gap-4 items-center">
                    <img className="h-12 md:h-14" alt="leagueIcon" src={`https://images.fotmob.com/image_resources/logo/leaguelogo/${props?.leagueId}.png`}></img>
                    <div className="flex flex-col gap-3">
                        <div className="text-xl md:text-2xl font-medium leading-4">{leagueData?.details?.name}</div>
                        <div className="text-sm md:text-lg text-[#6d6a7f] leading-3">{leagueData?.details?.country}</div>
                    </div>
                </div>
                <ul className="flex flex-wrap -mb-px text-sm font-medium text-center" id="default-tab" role="tablist">
                    {leagueData?.tabs?.slice(0, window.innerWidth <= 768 ? 4 : undefined).map((tab, index) => (
                        <li key={index} className={`cursor-pointer	 ${tab === activeTab ? "text-black border-black" : ""}`} role="presentation">
                        <div
                            className={`${tab === activeTab ? "text-black border-black" : ""} inline-block px-4 pb-4 border-b-2 border-gray-200 hover:opacity-50 rounded-t-lg text-xs text-[#6d6a7f] ${tab === activeTab ? "active-tab" : ""}`}
                            onClick={() => handleTabClick(tab)}
                            role="tab"
                            aria-controls={tab}
                            aria-selected={tab === activeTab ? "true" : "false"}
                        >
                            {tab.charAt(0).toUpperCase() + tab.slice(1)}
                        </div>
                        </li>
                    ))}
                </ul>
            </div>
            <div id="default-tab-content">
                {leagueData?.tabs?.map((tab, index) => (
                    <div key={index} className={`${tab === activeTab ? "" : "hidden"}`} id={tab} role="tabpanel" aria-labelledby={`${tab.toLowerCase()}-tab`}>
                    {tab === "overview" && 
                    <div className="flex flex-col gap-3">
                        <div className="p-4 bg-white brd rounded-lg flex flex-col gap-4">
                            <div className="flex justify-between">
                                <div className="hover:opacity-50 h-6 w-6 rounded-full bg-gray-200 brd flex items-center justify-center cursor-pointer">
                                    <img className="-rotate-90" alt="arrow" src="https://img.icons8.com/?size=512&id=86214&format=png"></img>
                                </div>  
                                <div className="text-lg font-medium">Matches</div>
                                <div className="hover:opacity-50 h-6 w-6 flex-nowrap rounded-full bg-gray-200 brd flex items-center justify-center cursor-pointer">
                                    <img className="rotate-90" alt="arrow" src="https://img.icons8.com/?size=512&id=86214&format=png"></img>
                                </div>  
                            </div>
                            <div className="gap-8 flex flex-col md:flex-row justify-between items-center">
                                <MatchCard matchData={leagueData?.matches?.allMatches?.[leagueData?.matches?.firstUnplayedMatch?.firstUnplayedMatchIndex]} />
                                <MatchCard matchData={leagueData?.matches?.allMatches?.[leagueData?.matches?.firstUnplayedMatch?.firstUnplayedMatchIndex+1]} />
                            </div>
                        </div>
                        <Standings id={props.leagueId} full={false}/>
                    </div>
                    }
                    {tab === "table" &&
                        <Standings id={props.leagueId} full={true}/>
                    } 
                    </div>
                ))}
            </div>
        </div>
    );
}

export default LeagueDetailsTop
