import { useParams } from "react-router-dom"
import ApiManager from "../api/ApiManager";
import React, { useEffect, useState } from "react";

const TeamDetail = () => {
    const {id} = useParams();
    const api = new ApiManager();
    const [teamData, setTeamData] = useState([]);

    useEffect(() => {
        const fetchStandings = async () => {
            try {
                const response = await api.makeRequest(`teams?id=${id}`);
                setTeamData(response.response[0]);
            } catch (error) {
                console.log(error);
            }
        };

        fetchStandings();
    }, []); 


	return (
		<>
			<div className="main flex">
				<div className="text-white flex flex-col w-full">
                    <div className="min-w-full h-72 rounded-bl-lg rounded-br-lg relative">
                        <img src="https://media.api-sports.io/football/venues/556.png" alt="teamVenue" className=" min-w-full h-72 object-cover" />
                        <div className=" min-w-full h-72 object-cover" />
                        <img src="https://media.api-sports.io/football/teams/33.png" className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 h-30 w-30" alt="teamLogo" />
                    </div>
				</div>
			</div>
		</>
	);
};

export default TeamDetail;
