import { useParams } from "react-router-dom"
import React, { useEffect, useState } from "react";
import SideBar from "../components/SideBar/Sidebar";
import TeamProfile from "../components/team/TeamProfile";
import LatestFuture from "../components/team/LatestFuture";
import PlayerCard from "../components/team/PlayerCard";
import Standings from "../components/standings/Standings";
import ProvaApi from "../api/ProvaApi";

const TeamDetail = (props) => {
    const {id} = useParams();
    const apiProva = new ProvaApi();
    const [teamData, setTeamData] = useState([]);
    const [venueData, setVenueData] = useState([]);

    useEffect(() => {
        const fetchTeam = async () => {
            try {
                const responseTeamData = await apiProva.makeRequest(`_next/data/4276/en/teams/${id}/overview/inter.json?id=8636&tab=overview&slug=inter`);
                console.log(responseTeamData.data.pageProps)
                setTeamData(responseTeamData.data.pageProps);
                // console.log(teamData.table[0].data.table.all)

            } catch (error) {
                console.log(error);
            }
        };

        fetchTeam(); 

    }, []);

	return (
		<>
            <SideBar />
			<div className="text-black flex flex-col p-4 sm:ml-64 gap-5">
                <TeamProfile teamName={teamData.details.name} teamNation={teamData.details.country} teamLogo={`https://images.fotmob.com/image_resources/logo/teamlogo/${teamData.details.id}.png`} teamVenueName={venueData.name || "Parc de Princes"} teamVenueCapacity={venueData.capacity || "66666"} teamVenueCity={venueData.city || "Paris"}/>
                <LatestFuture teamId={teamData.id || ""}/>
                <div className="flex flex-col gap-2">
                    <div className="flex justify-between">
                        <div className="text-sm text-[#6d6a7f]">First Team</div>
                        <div className="text-sm text-[#6d6a7f]">2023-24</div>
                    </div>
                    <div className="overflow-x-auto ">
                        <PlayerCard />
                    </div>
                </div>
                <div className="flex flex-col gap-2">
                    <div className="text-sm text-[#6d6a7f]">Season Leaderboard</div>
                    <Standings id={teamData.table || ""}/>
                </div>
            </div>	
		</>
	);
};

export default TeamDetail;
