import React from "react";
import {
    ResponsiveChartContainer,
    LineChart,
    ChartsYAxis,
    useDrawingArea,
    useYScale,
  } from '@mui/x-charts';

function Momentum(props) {
    const data = props.data?.content?.momentum?.main?.data
    console.log(data)

    let minutes = []
    let values = []

    data?.map((moment, index) => {
        minutes.push(moment.minute)
        values.push(moment.value)
    })

    function ColorSwitch({ threshold, color1, color2, id }) {
        const scale = useYScale()
        const off = scale(threshold);

        return (
            <defs className="w-100">
                <linearGradient id={id} x1="0" y1="0" x2="0" y2="1">
                    <stop offset={off} stopColor={color1} stopOpacity={1} />
                    <stop offset={off} stopColor={color2} stopOpacity={1} />
                </linearGradient>
            </defs>
        );
    }

    return (
        <div className="w-full flex flex-col rounded-lg py-4 bg-white brd gap-4">
            <div className="text-center font-medium text-base px-4">Match Momentum</div>
            <div className="brd05"></div>
            <div className="relative">
                <LineChart
                    axisHighlight={{
                        x: "none", 
                        y: "none",
                    }}
                    xAxis={[{
                        data: minutes,
                        min: minutes[0],
                        max: minutes[minutes.length-1],
                        hideTooltip: true
                    }]}
                    series={[
                        {
                            data: values,
                            area: true,
                            stack: "total",
                            showMark: false,
                            // color: props.colorData?.home
                        },
                    ]}
                    height={200}
                    leftAxis={null}
                    bottomAxis={null}
                    margin={{
                        left: 30,
                        right: 30,
                        top: 20,
                        bottom: 20,
                    }}
                    sx={{
                        "& .MUIAreaElement-root": {
                            fill: 'url(#swhich-color-id)'
                        }
                    }}
                >
                    <ColorSwitch color1={props.colorData?.home} color2={props.colorData?.away} threshold={0} id='swhich-color-id' />
                </LineChart>
                <div className=" absolute w-full bottom-3 pt-4 flex justify-center h-full">
                    <div className="border border-dashed border-gray-200 relative h-full" />
                </div>
                <div className=" absolute w-full bottom-3 px-4">
                    <div className="border border-dashed border-gray-200 relative w-full">
                        <div className="w-full right-0 absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-base flex justify-between">
                            <div className="w-fit px-2 py-0.5 bg-white rounded-full font-bold">
                                0'
                            </div>
                            <div className="w-fit px-2 py-0.5 bg-white rounded-full font-bold">
                                HT
                            </div>
                            <div className="w-fit px-2 py-0.5 bg-white rounded-full font-bold">
                                FT
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Momentum;
