import React, { useEffect, useState } from "react";
import StandingLine from "./StandingLine";
import ApiManager from "../../api/ApiManager"; 

function Standings(props) {
    const api = new ApiManager();
    const [standingData, setStandingData] = useState([]);
    const [leagueData, setLeagueData] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const responseStanding = await api.makeRequest(`standings?league=${props.leagueId}&season=2023`);
                setStandingData(responseStanding.response[0].league.standings[0]);
                setLeagueData(responseStanding.response[0].league);
                // const fakeData = Array.from({ length: 20 }, (_, index) => ({
                //     team: { id: 85, logo: `https://media.api-sports.io/football/teams/${index + 33}.png`, name: `Team ${index + 1}` },
                //     all: { win: index+2, draw: index, lose: index+5, played: index+10, dr: index+20 },
                //     points: index+4,
                //     rank: index + 1,
                //     index: index
                // }));

                // setStandingData(fakeData);
                // setLeagueData({ name: props.leagueName, icon: props.leagueIcon });
            } catch (error) {
                console.log(error);
            }
        };

        fetchData(); 

    }, []);

    return (
        <>
            <div className="flex flex-col gap-5 p-4 rounded-lg bg-gray-100">
                <div className="fixed flex justify-between relative">
                    <div className="flex gap-5 items-center basis-6/12">
                    <img className="h-10 w-10 object-contain" src={leagueData.logo} alt="leagueIcon" />
                        <div className="text-xl font-medium">{leagueData.name}</div> 
                    </div>
                    <div className="flex items-center basis-5/12 text-end">
                        <div className="text-[#6d6a7f] basis-4/12 md:block md:basis-2/12">G</div>
                        <div className="text-[#6d6a7f] basis-4/12 hidden md:block md:basis-2/12">V</div>
                        <div className="text-[#6d6a7f] basis-4/12 hidden md:block md:basis-2/12">N</div>
                        <div className="text-[#6d6a7f] basis-4/12 hidden md:block md:basis-2/12">P</div>
                        <div className="text-[#6d6a7f] basis-4/12 md:block md:basis-2/12">DR</div>
                        <div className="font-medium basis-4/12 md:block md:basis-2/12">PT</div>
                    </div>
                </div>
                <div className="max-h-96 overflow-y-auto flex flex-col gap-8">
                    {standingData.map((team, index) => (
                        <StandingLine
                            key={index}
                            index={team.index}
                            teamLogo={team.team.logo}
                            teamName={team.team.name}
                            teamWins={team.all.win}
                            teamDraws={team.all.draw}
                            teamLose={team.all.lose}
                            teamPoints={team.points}
                            teamPlayed={team.all.played}
                            number={team.rank}
                            teamId={team.team.id}
                            teamLeagueId={leagueData.team.league}
                            teamDR={team.goalsDiff}
                        />
                    ))}
                </div>
            </div>
        </>
    )
}

export default Standings;
