import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function StandingLine(props) {
    const [bgLine, setBgLine] = useState("#312e40");

    useEffect(() => {
        if (props.number <= 4) {
            setBgLine("champions");
        } else if (props.number == 5 ) {
            setBgLine("europa");
        } else if (props.number >= 18 && props.number <= 20) {
            setBgLine("retro")
        }
    }, [props.teamStreak, props.number]);

    return (
        <>
        <Link to={"/team/" + props.teamId}>
            <div className={`bg-${bgLine} p-4 rounded-lg flex gap-4`}>
                <div className="flex gap-5 items-center basis-6/12">
                    <div className="font-bold text-lg">{props.number || "1"}</div>
                    <img className="h-8" src={props.teamLogo || "https://media.api-sports.io/football/teams/33.png"} alt="teamLogo"/>
                    <div className="text-lg">{props.teamName || "Inter"}</div>
                </div>
                <div className="flex items-center basis-6/12 text">
                    <div className="text-lg basis-2/12">{props.teamPlayed || "20"}</div>
                    <div className="text-lg basis-2/12">{props.teamWins || "12"}</div>
                    <div className="text-lg basis-2/12">{props.teamDraws || "3"}</div>
                    <div className="text-lg basis-2/12">{props.teamLose || "2"}</div>
                    <div className="text-lg basis-2/12">{props.teamLose || "34"}</div>
                    <div className="text-lg font-bold basis-2/12">{props.teamPoints || "45"}</div>
                </div>
            </div>
        </Link>
        </>
    );
}

export default StandingLine;
