import axios from "axios";

class ProvaApi {
    axiosClient = axios.create();

    makeRequest = async (url) => {
        try { 
            const fullUrl = `https://corsproxy.io/?https://www.fotmob.com/${url}`;
            const response = await this.axiosClient({
                method: "GET",
                url: fullUrl,
                headers: {
                    "Sec-Fetch-Dest": "empty",
                    "Sec-Fetch-Mode": "cors",
                    "Sec-Fetch-Site": "cross-site"
                },
            });    
            return response;
        } catch (error) {
            // console.error(error);
            throw error;
        }
    };

    makeSearch = async (url) => {
        try { 
            const fullUrl = `https://corsproxy.io/?https://apigw.fotmob.com/${url}`;
            const response = await this.axiosClient({
                method: "GET",
                url: fullUrl,
                headers: {
                    'Cache-Control': 'no-cache',
                },
            });    
            return response;
        } catch (error) {
            // console.error(error);
            throw error;
        }
    };
}

export default ProvaApi;
