import { useParams } from "react-router-dom"
import React, { useEffect, useState } from "react";
import ProvaApi from "../api/ProvaApi";
import MatchDetailsTop from "../components/matches/MatchDetailsTop";
import Layout from "../components/Layout/Layout";

const MatchDetail = () => {
    const {id} = useParams();
    const apiProva = new ProvaApi();
    const [matchData, setMatchData] = useState([]);

    useEffect(() => {
        const fetchMatchData = async () => {
            try {
                const responseMatchData = await apiProva.makeRequest(`api/matchDetails?matchId=${id}`);
                setMatchData(responseMatchData.data);
            } catch (error) {
                console.log(error);
            }
        };

        fetchMatchData();

        const intervalId = setInterval(() => {
            // Update the live time in matchData
            setMatchData((prevData) => {
                if (prevData.header && prevData.header.status && prevData.header.status.liveTime) {
                    const updatedLiveTime = { ...prevData.header.status.liveTime };

                    // Increment minutes
                    updatedLiveTime.long += 1;
                    // Adjust hours if needed
                    if (updatedLiveTime.long >= 60) {
                        updatedLiveTime.long %= 60;
                        updatedLiveTime.short += 1;
                    }

                    return {
                        ...prevData,
                        header: {
                            ...prevData.header,
                            status: {
                                ...prevData.header.status,
                                liveTime: updatedLiveTime,
                            },
                        },
                    };
                }
                return prevData;
            });
        }, 1000);

        return () => clearInterval(intervalId);
    }, [id]);

	return (
		<>
            <Layout />
			<div className="text-black flex flex-col p-4 gap-5">
                <MatchDetailsTop matchData={matchData}/>
            </div>	
		</>
	);
};

export default MatchDetail;
