function LeagueMatches(props) {
    const leagueData = props.data

    const decrementMatchIndex = () => {
        setCurrentMatchIndex(prevIndex => Math.max(0, prevIndex - 1)); // Assicura che l'indice non scenda sotto 0
    };

    const incrementMatchIndex = () => {
        setCurrentMatchIndex(prevIndex => Math.min(leagueData?.matches?.allMatches?.length - 1, prevIndex + 1)); // Assicura che l'indice non superi la lunghezza dell'array
    };

    return (
        <div className="p-4 bg-white brd rounded-lg flex flex-col gap-4">
            <div className="flex justify-between">
                <div className="hover:opacity-50 h-6 w-6 rounded-full bg-gray-200 brd flex items-center justify-center cursor-pointer">
                    <img className="-rotate-90" alt="arrow" src="https://img.icons8.com/?size=512&id=86214&format=png"></img>
                </div>  
                <div className="text-lg font-medium">Matches</div>
                <div className="hover:opacity-50 h-6 w-6 flex-nowrap rounded-full bg-gray-200 brd flex items-center justify-center cursor-pointer">
                    <img className="rotate-90" alt="arrow" src="https://img.icons8.com/?size=512&id=86214&format=png"></img>
                </div>  
            </div>
            <div className="gap-8 flex flex-col md:flex-row justify-between items-center">
                <MatchCard matchData={leagueData?.matches?.allMatches?.[leagueData?.matches?.firstUnplayedMatch?.firstUnplayedMatchIndex]} />
                <MatchCard matchData={leagueData?.matches?.allMatches?.[leagueData?.matches?.firstUnplayedMatch?.firstUnplayedMatchIndex+1]} />
            </div>
        </div>
    )
}

export default LeagueMatches
