import React, { useEffect, useState } from "react";

function MatchDetailsTop(props) {
    const matchData = props.matchData
    console.log(props.matchData)

    const inputDateString = "Fri, Jan 12, 2024, 19:45 UTC";
    const dateObject = new Date(inputDateString);
    const timeFormatted = dateObject.toLocaleTimeString("en-US", { hour: "numeric", minute: "numeric", hour12: true });
    const today = new Date().toLocaleDateString("en-US", { month: "2-digit", day: "2-digit" });
    const [dateFormatted, setDateFormatted] = useState("");
    useEffect(() => {
        if (dateObject.toDateString() === new Date().toDateString()) {
            setDateFormatted("Today");
        } else {
            setDateFormatted(dateObject.toLocaleDateString("en-US", { month: "2-digit", day: "2-digit" }));
        }
    }, []);

    const date = new Date(matchData?.general?.matchTimeUTC);
        const options = { 
        year: "numeric", 
        month: "long", 
        day: "numeric", 
        hour: "numeric", 
        minute: "numeric", 
        // timeZoneName: "short" 
    };
    const matchDateFull = date.toLocaleDateString("en-US", options);


    return (
        <div className="min-w-full rounded-lg bg-gray-100 flex flex-col overflow-hidden">
            <div className="pt-4">
                <div className="flex justify-center gap-5 items-center pb-4">
                    <img alt="matchLeagueIcon" className="h-8" src={`https://images.fotmob.com/image_resources/logo/leaguelogo/${matchData?.general?.leagueId}.png`}></img>
                    <div className="text-base">{matchData?.general?.leagueName}</div>
                </div>
                <div className="border"></div>
            </div>
            <div className="flex p-4 justify-center items-center">
                <div className="flex basis-3/12 md:basis:5/12 gap-3 items-center justify-end">
                    <div className="text-lg hidden md:block">{matchData?.header?.teams?.[0]?.name}</div>
                    <img className="h-8 object-contain" src={`https://images.fotmob.com/image_resources/logo/teamlogo/${matchData?.header?.teams?.[0]?.id}.png`} alt="playerTeamLogo" />
                </div>
                <div className="flex basis-1/2 md:basis:1/6 justify-center">
                    {matchData?.header?.status?.scoreStr ? (
                        <div className="text-3xl font-medium">{matchData.header.status.scoreStr}</div>
                        ) : (
                            <>
                            <div className="flex flex-col gap-1 text-center">
                                <div className="text-3xl font-medium">{timeFormatted}</div>
                                <div className="text-xl font-medium text-[#6d6a7f]">{dateFormatted}</div>
                            </div>
                            </>
                    )}                
                </div>
                <div className="flex basis-3/12 md:basis:5/12 gap-3 items-center justify-start ">
                    <img className="h-8 object-contain " src={`https://images.fotmob.com/image_resources/logo/teamlogo/${matchData?.header?.teams?.[1]?.id}.png`} alt="playerTeamLogo" />
                    <div className="text-lg hidden md:block">{matchData?.header?.teams?.[1]?.name}</div>
                </div>
            </div>
            <div className="">
                <div className="border"></div>
                <div className="flex justify-center gap-5 items-center py-4">
                    {/* <img alt="matchLeagueIcon" className="h-8" src={`https://images.fotmob.com/image_resources/logo/leaguelogo/${matchData?.general?.leagueId}.png`}></img> */}
                    <div className="flex gap-2">
                        <img alt="stadiumIcon" className="h-6" src="https://img.icons8.com/?size=512&id=uZRLE51QnN2j&format=png" />
                        <div className="text-sm text-[#6d6a7f]">{matchData?.content?.matchFacts?.infoBox?.Stadium?.name}</div>
                    </div>
                    <div className="flex gap-2">
                        <img alt="stadiumIcon" className="h-6" src="https://img.icons8.com/?size=512&id=uZRLE51QnN2j&format=png" />
                        <div className="text-sm text-[#6d6a7f]">{matchData?.content?.matchFacts?.infoBox?.Stadium?.name}</div>
                    </div>
                    <div className="flex gap-2">
                        <img alt="whistleIcon" className="h-6" src="https://img.icons8.com/?size=512&id=uZ9JzCeXJmTf&format=png" />
                        <div className="text-sm text-[#6d6a7f]">{matchData?.content?.matchFacts?.infoBox?.Referee?.text}</div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default MatchDetailsTop
