import ApiManager from "../../api/ApiManager";
import React, { useEffect, useState } from "react";

function TransferCard(props) {
    const api = new ApiManager();
    const [transferData, setTransferData] = useState([]);

    // useEffect(() => {
    //     const fetchTransfer = async () => {
    //         try {
    //             const response = await api.makeRequest(`transfers?player=276`);
    //             setTransferData(response.response[0]);
    //         } catch (error) {
    //             console.log(error);
    //         }
    //     };

    //     fetchTransfer();
    // }, []); 

    // useEffect(() => {
    //     console.log(transferData);
    // }, [transferData]); 


    return (
        <div className="p-4 rounded-lg w-full bg-gray-100">
            <img className="h-10 object-fit" src={transferData.player.name || "Neymar"} alt="teamLogo" />
            <div className="flex justify-between">
                <div className="flex flex-col text-center gap-2 relative">
                    <div className="text-xs font-medium">LIV</div>
                    <span className="top-0 left-7 absolute w-3.5 h-3.5 bg-green-400 border-2 border-white rounded-full"></span>
                </div>
                <div className="flex gap-2 items-center">
                    {/* {matchData && (
                        <>
                            <div className="text-lg font-medium">{matchData.goals.home}</div>
                            <div className="text-lg font-medium">-</div>
                            <div className="text-lg font-medium">{matchData.goals.away}</div>
                        </>
                    )} */}
                    <div className="text-lg font-medium">1</div>
                    <div className="text-lg font-medium">-</div>
                    <div className="text-lg font-medium">2</div>
                </div>
                <div className="flex flex-col text-center gap-2">
                    <img className="h-10 object-fit" src="https://media.api-sports.io/football/teams/42.png" alt="teamLogo" />
                    <div className="text-xs font-medium">ARS</div>
                </div>
            </div>
            <div className="flex justify-center">
            {transferData.transfers[0].type === 1 ? (
            <span className="bg-green-100 text-green-800 text-xs font-medium px-2.5 py-0.5 rounded border border-green-400">FT</span>
            ) : props.isFinished === 2 ? (
            <span className="bg-pink-100 text-pink-800 text-xs font-medium px-2.5 py-0.5 rounded border border-pink-400">80'</span>
            ) : props.isFinished === 3 ? (
                <span className="bg-sky-100 text-sky-800 text-xs font-medium px-2.5 py-0.5 rounded border border-sky-400">18/01</span>
            ) : null}
            </div>
        </div>
    )
}

export default TransferCard
