import Standings from "../standings/Standing"

function LeagueDetailsTop(props) {    
    const leagueData = props?.data;
    console.log(leagueData?.tabs)
    const [activeTab, setActiveTab] = useState(leagueData?.tabs[0]); // Imposta il primo tab come attivo all'inizio

    const handleTabClick = (tab) => {
        setActiveTab(tab);
    };

    return(
        <div className="flex flex-col gap-3">
            <div className="flex flex-col px-4 pt-4 bg-white brd rounded-lg gap-12">
                <div className="flex gap-4 items-center">
                    <img className="h-14" alt="leagueIcon" src={`https://images.fotmob.com/image_resources/logo/leaguelogo/${props?.leagueId}.png`}></img>
                    <div className="flex flex-col gap-3">
                        <div className="text-2xl font-medium leading-4">{leagueData?.details?.name}</div>
                        <div className="text-lg text-[#6d6a7f] leading-3">{leagueData?.details?.country}</div>
                    </div>
                </div>
                <div className="flex flex-wrap -mb-px text-sm font-medium text-center gap-4" id="default-tab" data-tabs-toggle="#default-tab-content" role="tablist">
                    {leagueData?.tabs?.map((tab, index) => (
                        <div  key={index} className="inline-block px-4 pb-4 border-b-2 border-bg-gray-200 hover:text-gray-300 rounded-t-lg text-base text-[#6d6a7f]" id={tab} data-tabs-target={`#${tab}`} role="tab" aria-controls={tab}>
                            {tab.charAt(0).toUpperCase() + tab.slice(1)}
                        </div>
                    ))}
                </div>
            </div>
            <div className="flex flex-col px-4 pt-4 bg-white brd rounded-lg gap-12">
            <div id="default-tab-content">
                {leagueData?.tabs?.map((tab, index) => (
                    <div key={index} className={`hidden p-4 rounded-lg bg-gray-50 dark:bg-gray-800`} id={tab.toLowerCase()} role="tabpanel" aria-labelledby={`${tab.toLowerCase()}-tab`}>
                        {tab === "overview" && <Standings id={props.leagueId} />}
                    </div>
                ))}
            </div>
            </div>
        </div>

    )
}

export default LeagueDetailsTop
