import React, { useEffect, useState } from "react";
import ApiManager from "./api/ApiManager";
import Standings from "./components/standings/Standings";

const App = () => {
	const api = new ApiManager();
	const [matches, setMatches] = useState({});

	const matchesApi = async () => {
		try {
			const responseMatches = await api.makeRequest("standings", "GET", {season: 2023, league: 39});
			setMatches(responseMatches);
			console.log(responseMatches);
		} catch (error) {
			console.log(error);
		}
	};

	useEffect(() => {
		matchesApi();
	}, []);

	const leagueIcon = matches.response?.[0]?.league?.logo
	const leagueName = matches.response?.[0]?.league?.name


	return (
		<>
		<div className="main p-4 flex">
			<div className="text-white text-white flex flex-col p-4 w-full">
				<Standings leagueIcon="https://media.api-sports.io/football/leagues/39.png" leagueName="Premier League" leagueFlag="https://media.api-sports.io/flags/gb.svg"/>
			</div>
		</div>
		</>
	);
};

export default App;
