import { useState } from "react";
import MatchCard from "../matches/MatchCard";
import Standings from "../standings/Standing"

function LeagueDetailsTop(props) {    
    const leagueData = props?.data;
    console.log(leagueData?.matches?.allMatches)
    const [activeTab, setActiveTab] = useState(leagueData?.tabs?.[0]); 

    const handleTabClick = (tab) => {
        setActiveTab(tab);
    };

    return(
        <div className="flex flex-col gap-3">
            <div className="flex flex-col px-4 pt-4 bg-white brd rounded-lg gap-12">
                <div className="flex gap-4 items-center">
                    <img className="h-14" alt="leagueIcon" src={`https://images.fotmob.com/image_resources/logo/leaguelogo/${props?.leagueId}.png`}></img>
                    <div className="flex flex-col gap-3">
                        <div className="text-2xl font-medium leading-4">{leagueData?.details?.name}</div>
                        <div className="text-lg text-[#6d6a7f] leading-3">{leagueData?.details?.country}</div>
                    </div>
                </div>
                <ul className="flex flex-wrap -mb-px text-sm font-medium text-center" id="default-tab" role="tablist">
                    {leagueData?.tabs?.map((tab, index) => (
                        <li key={index} className={`cursor-pointer	 ${tab === activeTab ? "color-black border-black" : ""}`} role="presentation">
                        <div
                            className={`${tab === activeTab ? "text-black border-black" : ""} inline-block px-4 pb-4 border-b-2 border-gray-200 hover:opacity-50 rounded-t-lg text-base text-[#6d6a7f] ${tab === activeTab ? "active-tab" : ""}`}
                            onClick={() => handleTabClick(tab)}
                            role="tab"
                            aria-controls={tab}
                            aria-selected={tab === activeTab ? "true" : "false"}
                        >
                            {tab.charAt(0).toUpperCase() + tab.slice(1)}
                        </div>
                        </li>
                    ))}
                </ul>
            </div>
            <div id="default-tab-content">
                {leagueData?.tabs?.map((tab, index) => (
                    <div key={index} className={`${tab === activeTab ? "" : "hidden"}`} id={tab} role="tabpanel" aria-labelledby={`${tab.toLowerCase()}-tab`}>
                    {tab === "overview" && 
                    <div className="flex flex-col gap-3">
                        <div className="p-4 bg-white brd rounded-lg gap-14 justify-center items-center">
                            {leagueData?.matches?.allMatches?.[leagueData?.matches?.firstUnplayedMatchIndex]}
                            {leagueData?.matches?.allMatches?.[leagueData?.matches?.firstUnplayedMatchIndex+1]}
                        </div>
                        {/* <MatchCard fixtureData={leagueData?.matches?.allMatches}/> */}
                        <Standings id={props.leagueId} />
                    </div>
                    }
                    </div>
                ))}
            </div>
        </div>
    );
}

export default LeagueDetailsTop
