import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import ProvaApi from "../api/ProvaApi";
import Layout from "../components/Layout/Layout";

const LeagueList = () => {
    const apiProva = new ProvaApi();
    const [leagueListData, setleagueListData] = useState([]);

    useEffect(() => {
        const fetchLeagueList = async () => {
            try {
                const responseleagueListData = await apiProva.makeRequest(`api/allLeagues`);
                setleagueListData(responseleagueListData.data);  
            } catch (error) {
                console.log(error);
            }
        };

        fetchLeagueList(); 

    }, []);

    console.log(leagueListData)

	return (
		<>
			<Layout />
			<div className="text-black flex flex-col p-4 gap-3">
				<div className="flex flex-col md:flex-row gap-3 w-100">	
					<div className="w-100 flex flex-col bg-white brd rounded-lg py-4 gap-5">
                        <div className="px-4 text-lg font-medium">Top Leagues</div>
                        <div className="brd05"></div>
                        {leagueListData?.popular.map((league, index) => (
                            <Link to={"/league-details/" + (league.id)}>
                                <div className="flex gap-3">
                                    <img className="h-6 md:h-8" alt="leagueIcon" src={`https://images.fotmob.com/image_resources/logo/leaguelogo/${props?.leagueId}.png`}></img>
                                </div>
                            </Link>
                        ))}
                    </div>
				</div>
			</div>
		</>
	);
};

export default LeagueList;
