import axios from "axios";

class ApiManager {
    axiosClient = axios.create();
    apiKey = process.env.REACT_APP_API_KEY;

    async initialize() {     
        this.axiosClient.defaults.baseURL = process.env.REACT_APP_BASE_URL;
    }

    makeRequest = async (url, url2 = "", headers = {}) => {
        await this.initialize();

        const axiosOptions = {
            method: "GET",
            url: `${url}`,
            headers: {
                "Access-Control-Allow-Origin": "*",
                "X-Auth-Token": "your_auth_token",
            },
        };

        try {
            const response = await this.axiosClient(axiosOptions);
            return response;
        } catch (error) {
            console.log("Error:", error);
            throw error;
        }
    }

    post = async (url, payload, headers = {}) => {
        await this.initialize();

        const axiosOptions = {
            method: "POST",
            url: `${url}?api_key=${this.apiKey}`,
            data: payload,
            headers: {
                "Access-Control-Allow-Origin": "*",
                "X-Auth-Token": "your_auth_token",
            },
        };

        try {
            const response = await this.axiosClient(axiosOptions);
            return response;
        } catch (error) {
            console.log("Error:", error);
            throw error;
        }
    }
}

export default ApiManager;
