    // useEffect(() => {
    //     const fetchStandings = async () => {
    //         try {
    //             const response = await api.makeRequest(`fixtures?team=33&season=2023`);
    //             setMatchData(response.response[0]);
    //         } catch (error) {
    //             console.log(error);
    //         }
    //     };

    //     fetchStandings();
    // }, []); 

    // useEffect(() => {
    //     console.log(matchData);
    // }, [matchData]); 


    return (
        <div className="p-4 rounded-lg bg-gray-100 transition ease-in-out text-gray-800 group hover:shadow">
            <div className="flex justify-between">
                <div className="flex flex-col text-center gap-2 relative">
                    <img className="h-10 object-fit" src="https://media.api-sports.io/football/teams/40.png" alt="teamLogo" />
                    <div className="text-xs font-medium">LIV</div>
                    <span className="top-0 left-7 absolute w-3.5 h-3.5 bg-green-400 border-2 border-white rounded-full"></span>
                </div>
                <div className="flex gap-2 items-center">
                    {/* {matchData && (
                        <>
                            <div className="text-lg font-medium">{matchData.goals.home}</div>
                            <div className="text-lg font-medium">-</div>
                            <div className="text-lg font-medium">{matchData.goals.away}</div>
                        </>
                    )} */}
                    {matchData && (
                        <>
                            <div className="text-lg font-medium">{matchData.goals.home}</div>
                            <div className="text-lg font-medium">-</div>
                            <div className="text-lg font-medium">{matchData.goals.away}</div>
                        </>
                    )}
                </div>
                <div className="flex flex-col text-center gap-2">
                    <img className="h-10 object-fit" src="https://media.api-sports.io/football/teams/42.png" alt="teamLogo" />
                    <div className="text-xs font-medium">ARS</div>
                </div>
            </div>
            <div className="flex justify-center">
                {props.isFinished ? (
                    <span className="bg-green-100 text-green-800 text-xs font-medium px-2.5 py-0.5 rounded border border-green-400">Full Time</span>
                ) : (
                    <span className="bg-pink-100 text-pink-800 text-xs font-medium px-2.5 py-0.5 rounded border border-pink-400">80'</span>
                )}
            </div>
        </div>
    )
}

export default MatchCard
